import React from 'react';
import { Link } from 'react-router-dom';
import BusinessCard from '../components/BusinessCard';

const Homepage = () => {
  // Mock data for carousels
  const featuredDeals = [
    {
      id: 1,
      name: "The Coffee House",
      category: "CafÃ©",
      neighborhood: "An Thuong",
      rating: 4.5,
      reviewCount: 128,
      hasMemberDeal: true,
      dealHighlight: "20% off all drinks",
      coverPhoto: "https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=400&h=300&fit=crop"
    },
    {
      id: 2,
      name: "Beach Yoga Studio",
      category: "Wellness",
      neighborhood: "Non Nuoc",
      rating: 4.8,
      reviewCount: 67,
      hasMemberDeal: true,
      dealHighlight: "Free class for new members",
      coverPhoto: "https://images.unsplash.com/photo-1544367567-0f2fcb009e0b?w=400&h=300&fit=crop"
    },
    {
      id: 3,
      name: "Digital Nomad Hub",
      category: "Co-working",
      neighborhood: "Hai Chau",
      rating: 4.6,
      reviewCount: 45,
      hasMemberDeal: true,
      dealHighlight: "50% off first month",
      coverPhoto: "https://images.unsplash.com/photo-1497366216548-37526070297c?w=400&h=300&fit=crop"
    }
  ];

  const popularBusinesses = [
    {
      id: 4,
      name: "Pho 24",
      category: "Restaurant",
      neighborhood: "My Khe",
      rating: 4.2,
      reviewCount: 89,
      hasMemberDeal: false,
      coverPhoto: "https://images.unsplash.com/photo-1555939594-58d7cb561ad1?w=400&h=300&fit=crop"
    },
    {
      id: 5,
      name: "Local Market Tours",
      category: "Experience",
      neighborhood: "Han Market",
      rating: 4.7,
      reviewCount: 156,
      hasMemberDeal: true,
      dealHighlight: "Group discount for 3+ people",
      coverPhoto: "https://images.unsplash.com/photo-1556909114-f6e7ad7d3136?w=400&h=300&fit=crop"
    },
    {
      id: 6,
      name: "Artisan Bakery",
      category: "Bakery",
      neighborhood: "Hai Chau",
      rating: 4.4,
      reviewCount: 92,
      hasMemberDeal: true,
      dealHighlight: "Buy 2 get 1 free on pastries",
      coverPhoto: "https://images.unsplash.com/photo-1509440159596-0249088772ff?w=400&h=300&fit=crop"
    }
  ];

  const upcomingEvents = [
    {
      id: 1,
      title: "Coffee Tasting Workshop",
      date: "Jan 15",
      time: "2:00 PM",
      businessName: "The Coffee House",
      image: "https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=400&h=300&fit=crop"
    },
    {
      id: 2,
      title: "Sunset Beach Yoga",
      date: "Jan 20",
      time: "5:30 PM",
      businessName: "Beach Yoga Studio",
      image: "https://images.unsplash.com/photo-1544367567-0f2fcb009e0b?w=400&h=300&fit=crop"
    },
    {
      id: 3,
      title: "Vietnamese Cooking Class",
      date: "Jan 25",
      time: "10:00 AM",
      businessName: "Local Market Tours",
      image: "https://images.unsplash.com/photo-1556909114-f6e7ad7d3136?w=400&h=300&fit=crop"
    }
  ];

  const handleToggleFavorite = (businessId, isFavorite) => {
    // TODO: Update Strapi API
    console.log(`Business ${businessId} ${isFavorite ? 'favorited' : 'unfavorited'}`);
  };

  const CarouselSection = ({ title, viewAllLink, children, className = "" }) => (
    <section className={`mb-12 ${className}`}>
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold text-text">{title}</h2>
        <Link 
          to={viewAllLink} 
          className="text-primary hover:text-primary-dark font-medium transition-colors duration-200"
        >
          View All â†’
        </Link>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {children}
      </div>
    </section>
  );

  const EventCard = ({ event }) => (
    <Link to={`/events/${event.id}`} className="block">
      <div className="card overflow-hidden hover:shadow-md transition-shadow duration-200">
        <div className="h-48 bg-gray-200 overflow-hidden">
          <img
            src={event.image}
            alt={event.title}
            className="w-full h-full object-cover hover:scale-105 transition-transform duration-300"
          />
        </div>
        <div className="p-4">
          <h3 className="font-semibold text-lg text-text mb-2">{event.title}</h3>
          <div className="space-y-1 text-sm text-text-secondary">
            <div className="flex items-center space-x-2">
              <span className="font-medium">{event.date}</span>
              <span>â€¢</span>
              <span>{event.time}</span>
            </div>
            <p>{event.businessName}</p>
          </div>
        </div>
      </div>
    </Link>
  );

  return (
    <div className="min-h-screen bg-background-gray">
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-primary to-primary-dark text-white py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-6">
            Discover Amazing Places
          </h1>
          <p className="text-xl md:text-2xl text-blue-100 max-w-3xl mx-auto mb-8">
            Explore the best businesses, exclusive member deals, and exciting events in Da Nang & Hoi An
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link to="/directory" className="btn-secondary bg-white text-primary hover:bg-gray-50">
              Browse Directory
            </Link>
            <Link to="/deals" className="btn-primary bg-white text-primary hover:bg-gray-50">
              View Member Deals
            </Link>
          </div>
        </div>
      </section>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Featured Member Deals */}
        <CarouselSection title="âœ¨ Featured Member Deals" viewAllLink="/deals">
          {featuredDeals.map(business => (
            <BusinessCard
              key={business.id}
              business={business}
              onToggleFavorite={handleToggleFavorite}
            />
          ))}
        </CarouselSection>

        {/* Popular Businesses */}
        <CarouselSection title="ðŸ”¥ Popular Businesses" viewAllLink="/directory">
          {popularBusinesses.map(business => (
            <BusinessCard
              key={business.id}
              business={business}
              onToggleFavorite={handleToggleFavorite}
            />
          ))}
        </CarouselSection>

        {/* Upcoming Events */}
        <CarouselSection title="ðŸ“… Upcoming Events" viewAllLink="/events">
          {upcomingEvents.map(event => (
            <EventCard key={event.id} event={event} />
          ))}
        </CarouselSection>

        {/* CTA Section */}
        <section className="text-center py-16">
          <div className="card p-12 max-w-2xl mx-auto">
            <h2 className="text-3xl font-bold text-text mb-4">
              Ready to Explore?
            </h2>
            <p className="text-text-secondary text-lg mb-8">
              Join thousands of members discovering the best of Da Nang & Hoi An
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <button className="btn-primary">
                Get Started
              </button>
              <button className="btn-secondary">
                Learn More
              </button>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Homepage;
