import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { HeartIcon, StarIcon, MapPinIcon } from '@heroicons/react/24/outline';
import { HeartIcon as HeartSolidIcon } from '@heroicons/react/24/solid';

const BusinessCard = ({ business, onToggleFavorite, isFavorite = false }) => {
  const [favorite, setFavorite] = useState(isFavorite);

  const handleFavoriteToggle = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setFavorite(!favorite);
    if (onToggleFavorite) {
      onToggleFavorite(business.id, !favorite);
    }
  };

  return (
    <Link to={`/business/${business.id}`} className="block">
      <div className="card overflow-hidden hover:shadow-medium transition-shadow duration-200 group">
        {/* Cover Photo */}
        <div className="relative h-48 bg-gray-200 overflow-hidden">
          <img
            src={business.coverPhoto || 'https://images.unsplash.com/photo-1552566626-52f8b828add9?w=400&h=300&fit=crop'}
            alt={business.name}
            className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
          />
          
          {/* Favorite Button */}
          <button
            onClick={handleFavoriteToggle}
            className="absolute top-3 right-3 p-2 bg-white/90 backdrop-blur-sm rounded-full shadow-soft hover:bg-white transition-all duration-200"
          >
            {favorite ? (
              <HeartSolidIcon className="w-5 h-5 text-accent" />
            ) : (
              <HeartIcon className="w-5 h-5 text-text-secondary" />
            )}
          </button>

          {/* Member Deal Badge */}
          {business.hasMemberDeal && (
            <div className="absolute top-3 left-3 px-3 py-1 bg-accent text-white text-xs font-semibold rounded-full shadow-soft">
              Member Deal
            </div>
          )}
        </div>

        {/* Content */}
        <div className="p-4">
          {/* Business Name & Category */}
          <div className="mb-2">
            <h3 className="font-poppins font-semibold text-lg text-text group-hover:text-accent transition-colors duration-200">
              {business.name}
            </h3>
            <div className="flex items-center space-x-2 text-text-secondary text-sm">
              <span>{business.category}</span>
              <span>•</span>
              <div className="flex items-center space-x-1">
                <MapPinIcon className="w-4 h-4" />
                <span>{business.neighborhood}</span>
              </div>
            </div>
          </div>

          {/* Rating & Reviews */}
          <div className="flex items-center justify-between mb-3">
            <div className="flex items-center space-x-1">
              <StarIcon className="w-4 h-4 text-yellow-400 fill-current" />
              <span className="text-sm font-medium text-text">
                {business.rating}
              </span>
              <span className="text-sm text-text-secondary">
                ({business.reviewCount} reviews)
              </span>
            </div>
          </div>

          {/* Deal Highlight */}
          {business.hasMemberDeal && business.dealHighlight && (
            <div className="bg-accent/10 border border-accent/20 rounded-lg p-3">
              <p className="text-sm text-accent font-medium">
                {business.dealHighlight}
              </p>
            </div>
          )}
        </div>
      </div>
    </Link>
  );
};

export default BusinessCard;
