import React, { useState, useEffect } from 'react';
import { MagnifyingGlassIcon, FunnelIcon, MapPinIcon, StarIcon } from '@heroicons/react/24/outline';
import BusinessCard from '../components/BusinessCard';

const Directory = () => {
  const [businesses, setBusinesses] = useState([]);
  const [filteredBusinesses, setFilteredBusinesses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilters, setActiveFilters] = useState({
    category: [],
    hasMemberDeal: false,
    amenities: [],
    openNow: false
  });
  const [loading, setLoading] = useState(true);
  const [showFilters, setShowFilters] = useState(false);

  // Mock data - in real app this would come from Strapi API
  useEffect(() => {
    const mockBusinesses = [
      {
        id: 1,
        name: "The Coffee House",
        category: "Caf√©",
        neighborhood: "An Thuong",
        rating: 4.5,
        reviewCount: 128,
        hasMemberDeal: true,
        dealHighlight: "20% off all drinks for members",
        coverPhoto: "https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=400&h=300&fit=crop",
        amenities: ["WiFi", "Power Outlets", "Air Conditioning"],
        isOpen: true
      },
      {
        id: 2,
        name: "Pho 24",
        category: "Restaurant",
        neighborhood: "My Khe",
        rating: 4.2,
        reviewCount: 89,
        hasMemberDeal: false,
        coverPhoto: "https://images.unsplash.com/photo-1555939594-58d7cb561ad1?w=400&h=300&fit=crop",
        amenities: ["Air Conditioning", "Outdoor Seating"],
        isOpen: true
      },
      {
        id: 3,
        name: "Beach Yoga Studio",
        category: "Wellness",
        neighborhood: "Non Nuoc",
        rating: 4.8,
        reviewCount: 67,
        hasMemberDeal: true,
        dealHighlight: "Free class for new members",
        coverPhoto: "https://images.unsplash.com/photo-1544367567-0f2fcb009e0b?w=400&h=300&fit=crop",
        amenities: ["Parking", "Shower Facilities"],
        isOpen: false
      },
      {
        id: 4,
        name: "Digital Nomad Hub",
        category: "Co-working",
        neighborhood: "Hai Chau",
        rating: 4.6,
        reviewCount: 45,
        hasMemberDeal: true,
        dealHighlight: "50% off first month",
        coverPhoto: "https://images.unsplash.com/photo-1497366216548-37526070297c?w=400&h=300&fit=crop",
        amenities: ["WiFi", "Power Outlets", "Meeting Rooms", "Coffee"],
        isOpen: true
      },
      {
        id: 5,
        name: "Local Market Tours",
        category: "Experience",
        neighborhood: "Han Market",
        rating: 4.7,
        reviewCount: 156,
        hasMemberDeal: true,
        dealHighlight: "Group discount for 3+ people",
        coverPhoto: "https://images.unsplash.com/photo-1556909114-f6e7ad7d3136?w=400&h=300&fit=crop",
        amenities: ["Guide", "Transportation"],
        isOpen: true
      },
      {
        id: 6,
        name: "Artisan Bakery",
        category: "Bakery",
        neighborhood: "Hai Chau",
        rating: 4.4,
        reviewCount: 92,
        hasMemberDeal: true,
        dealHighlight: "Buy 2 get 1 free on pastries",
        coverPhoto: "https://images.unsplash.com/photo-1509440159596-0249088772ff?w=400&h=300&fit=crop",
        amenities: ["Outdoor Seating", "Coffee"],
        isOpen: false
      }
    ];

    setBusinesses(mockBusinesses);
    setFilteredBusinesses(mockBusinesses);
    setLoading(false);
  }, []);

  // Apply filters and search
  useEffect(() => {
    let filtered = businesses;

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(business =>
        business.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        business.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
        business.neighborhood.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Category filter
    if (activeFilters.category.length > 0) {
      filtered = filtered.filter(business =>
        activeFilters.category.includes(business.category)
      );
    }

    // Member deal filter
    if (activeFilters.hasMemberDeal) {
      filtered = filtered.filter(business => business.hasMemberDeal);
    }

    // Amenities filter
    if (activeFilters.amenities.length > 0) {
      filtered = filtered.filter(business =>
        activeFilters.amenities.some(amenity => business.amenities.includes(amenity))
      );
    }

    // Open now filter
    if (activeFilters.openNow) {
      filtered = filtered.filter(business => business.isOpen);
    }

    setFilteredBusinesses(filtered);
  }, [businesses, searchTerm, activeFilters]);

  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  const handleCategoryFilter = (category) => {
    setActiveFilters(prev => ({
      ...prev,
      category: prev.category.includes(category)
        ? prev.category.filter(c => c !== category)
        : [...prev.category, category]
    }));
  };

  const handleAmenityFilter = (amenity) => {
    setActiveFilters(prev => ({
      ...prev,
      amenities: prev.amenities.includes(amenity)
        ? prev.amenities.filter(a => a !== amenity)
        : [...prev.amenities, amenity]
    }));
  };

  const handleToggleFavorite = (businessId, isFavorite) => {
    // TODO: Update Strapi API
    console.log(`Business ${businessId} ${isFavorite ? 'favorited' : 'unfavorited'}`);
  };

  const clearAllFilters = () => {
    setActiveFilters({
      category: [],
      hasMemberDeal: false,
      amenities: [],
      openNow: false
    });
    setSearchTerm('');
  };

  const categories = ["Caf√©", "Restaurant", "Wellness", "Co-working", "Experience", "Bakery"];
  const allAmenities = ["WiFi", "Power Outlets", "Air Conditioning", "Outdoor Seating", "Parking", "Meeting Rooms", "Coffee", "Shower Facilities", "Guide", "Transportation"];

  if (loading) {
    return (
      <div className="min-h-screen bg-background-gray flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
          <p className="mt-4 text-text-secondary">Loading businesses...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background-gray">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-text mb-4">
            Business Directory
          </h1>
          <p className="text-xl text-text-secondary max-w-2xl mx-auto">
            Discover the best businesses, restaurants, and experiences in Da Nang & Hoi An
          </p>
        </div>

        {/* Search and Filters */}
        <div className="mb-8">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between space-y-4 lg:space-y-0">
            {/* Search Bar */}
            <div className="flex-1 max-w-2xl">
              <div className="relative">
                <MagnifyingGlassIcon className="absolute left-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-text-secondary" />
                <input
                  type="text"
                  placeholder="Search businesses, restaurants, cafes..."
                  value={searchTerm}
                  onChange={(e) => handleSearch(e.target.value)}
                  className="w-full pl-12 pr-4 py-4 bg-white border border-border rounded-default focus:ring-2 focus:ring-primary focus:border-transparent transition-all duration-200 shadow-sm"
                />
              </div>
            </div>
            
            {/* Filter Toggle */}
            <button
              onClick={() => setShowFilters(!showFilters)}
              className="flex items-center space-x-2 text-text-secondary hover:text-text transition-colors duration-200"
            >
              <FunnelIcon className="w-5 h-5" />
              <span className="font-medium">Filters</span>
            </button>
          </div>
        </div>

        {/* Filters Panel */}
        {showFilters && (
          <div className="card p-6 mb-8">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-text">Filters</h3>
              <button
                onClick={clearAllFilters}
                className="text-sm text-primary hover:text-primary-dark transition-colors duration-200"
              >
                Clear All
              </button>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {/* Category Filter */}
              <div>
                <h4 className="font-medium text-text mb-3">Category</h4>
                <div className="space-y-2">
                  {categories.map((category) => (
                    <label key={category} className="flex items-center space-x-2">
                      <input
                        type="checkbox"
                        checked={activeFilters.category.includes(category)}
                        onChange={() => handleCategoryFilter(category)}
                        className="rounded border-border text-primary focus:ring-primary"
                      />
                      <span className="text-sm text-text-secondary">{category}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* Member Deal Filter */}
              <div>
                <h4 className="font-medium text-text mb-3">Member Benefits</h4>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={activeFilters.hasMemberDeal}
                    onChange={(e) => setActiveFilters(prev => ({ ...prev, hasMemberDeal: e.target.checked }))}
                    className="rounded border-border text-primary focus:ring-primary"
                  />
                  <span className="text-sm text-text-secondary">Has Member Deal</span>
                </label>
              </div>

              {/* Amenities Filter */}
              <div>
                <h4 className="font-medium text-text mb-3">Amenities</h4>
                <div className="space-y-2 max-h-32 overflow-y-auto">
                  {allAmenities.map((amenity) => (
                    <label key={amenity} className="flex items-center space-x-2">
                      <input
                        type="checkbox"
                        checked={activeFilters.amenities.includes(amenity)}
                        onChange={() => handleAmenityFilter(amenity)}
                        className="rounded border-border text-primary focus:ring-primary"
                      />
                      <span className="text-sm text-text-secondary">{amenity}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* Open Now Filter */}
              <div>
                <h4 className="font-medium text-text mb-3">Status</h4>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={activeFilters.openNow}
                    onChange={(e) => setActiveFilters(prev => ({ ...prev, openNow: e.target.checked }))}
                    className="rounded border-border text-primary focus:ring-primary"
                  />
                  <span className="text-sm text-text-secondary">Open Now</span>
                </label>
              </div>
            </div>
          </div>
        )}

        {/* Results Count */}
        <div className="mb-6">
          <p className="text-text-secondary">
            {filteredBusinesses.length} business{filteredBusinesses.length !== 1 ? 'es' : ''} found
            {searchTerm && ` for "${searchTerm}"`}
            {Object.values(activeFilters).some(filter => 
              Array.isArray(filter) ? filter.length > 0 : filter
            ) && ' with applied filters'}
          </p>
        </div>

        {/* Business Listings */}
        {filteredBusinesses.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredBusinesses.map(business => (
              <BusinessCard
                key={business.id}
                business={business}
                onToggleFavorite={handleToggleFavorite}
              />
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">üîç</div>
            <h3 className="text-lg font-semibold text-text mb-2">No businesses found</h3>
            <p className="text-text-secondary mb-4">
              Try adjusting your search terms or filters.
            </p>
            <button
              onClick={clearAllFilters}
              className="btn-secondary"
            >
              Clear All Filters
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Directory;
